import {Injectable} from '@angular/core';
import {HttpClient, HttpHeaders} from '@angular/common/http';
import {Router} from '@angular/router';
import {throwError} from 'rxjs';
import {catchError, map} from 'rxjs/operators';
import {server} from '../server';
import {TranslatePipe} from '../../pipes/translate/translate.pipe';

@Injectable()
export class AuthService {
  constructor(private http: HttpClient,
              private translate: TranslatePipe,
              private router: Router) {
  }

  TOKEN_KEY = 'token';

  ServerGet(address: string) {
    const token = localStorage.getItem(this.TOKEN_KEY);
    const lang = localStorage.getItem('lang');
    const headers = new HttpHeaders()
      .set('Accept', 'application/json')
      .set('Cache-Control', ' no-cache')
      .set('Content-Type', 'application/json')
      .set('Accept-Language', lang)
      .set('Authorization', 'Token ' + token);
    const options = {headers: headers};
    return this.http.get(address, options)
      .pipe(
        map(res => res),
        catchError((err) => {
          this.ServerError(err);
          return throwError(err);
        }));
  }

  ServerGetLogin(address: string) {
    const lang = localStorage.getItem('lang');
    const headers = new HttpHeaders()
      .set('Accept', 'application/json')
      .set('Cache-Control', ' no-cache')
      .set('Content-Type', 'application/json')
      .set('Accept-Language', lang);
    const options = {headers: headers};
    return this.http.get(address, options)
      .pipe(
        map(res => res),
        catchError((err) => {
          this.ServerError(err);
          return throwError(err);
        }));
  }

  ServerPost(address: string, data: any) {
    const token = localStorage.getItem(this.TOKEN_KEY);
    const lang = localStorage.getItem('lang');
    const headers = new HttpHeaders()
      .set('Accept', 'application/json')
      .set('Cache-Control', ' no-cache')
      .set('Content-Type', 'application/json')
      .set('Accept-Language', lang)
      .set('Authorization', 'Token ' + token);
    const options = {headers: headers};
    return this.http.post(address, data, options)
      .pipe(
        map(res => res),
        catchError((err) => {
          this.ServerError(err);
          return throwError(err);
        }));
  }

  ServerPostLogin(address: string, data: any) {
    const lang = localStorage.getItem('lang');
    const headers = new HttpHeaders()
      .set('Accept', 'application/json')
      .set('Cache-Control', ' no-cache')
      .set('Content-Type', 'application/json')
      .set('Accept-Language', lang);
    const options = {headers: headers};
    return this.http.post(address, data, options)
      .pipe(
        map(res => res),
        catchError((err) => {
          this.ServerError(err);
          return throwError(err);
        }));
  }

  saveToken(token) {
    localStorage.setItem(this.TOKEN_KEY, token);
  }


  ClearSession() {
    localStorage.clear();
    sessionStorage.clear();
    localStorage.setItem('lang', 'tr');
    this.router.navigate(['/']);
  }

  ServerLogout() {
    this.ServerGet(server.sessionClose)
      .subscribe(res => {
        this.ClearSession();
      }, err => {
      });
  }

  token() {
    return localStorage.getItem(this.TOKEN_KEY);
  }

  ServerError(err) {
    if (err.status === 200 || err.status === 500) {
      // @ts-ignore
      alertify.alert(
        '',
        "<center><img style='width: 60px; height: 60px;' src='data:image/png;base64,'></center><br>" +
        "<center><b>" + this.translate.transform('ConError', []) + "</b></center><br>" + "<center><p class='notification-message'>" + this.translate.transform('ConErrorInfo', []) + "</p></center>"
      ).setting({
        'label': this.translate.transform('Okay', []),
      });
    } else if (err.status === 401 && err.error.detail === 'token.expire') {
      this.ServerLogout()
      // @ts-ignore
      alertify.alert(
        '',
        "<center><img style='width: 60px; height: 60px;' src='data:image/png;base64,'></center><br>" +
        "<center><b>" + this.translate.transform('ConError', []) + "</b></center><br>" + "<center><p>" + this.translate.transform('SessionExpired', []) + "</p></center>"
      ).setting({
        'label': this.translate.transform('Okay', []),
      });
    } else if (err.status === 401) {
      this.router.navigateByUrl('/');
    }
  }

  CheckAccess() {
    const token = localStorage.getItem(this.TOKEN_KEY);
    return JSON.parse(window.atob((token).split('.')[1])).userType;
  }

}
